#!/usr/bin/env bash
set -ex

sudo touch /var/www/html/index.php
sudo chown @@{superuser.username}@@:@@{superuser.username}@@ /var/www/html/index.php

cat > /var/www/html/index.php <<- "EOF"
<html>
<head>
  <title>@@{calm_blueprint_name}@@</title>
</head>
<body>
 <h1>Hostname: @@{name}@@ at @@{address}@@, replica: @@{calm_array_index}@@</h1>
<?php
try {
  $dbh = new PDO('mysql:host=@@{MySQL.address}@@;dbname=@@{DATABASE_NAME}@@', '@@{MYSQL_APP_USER}@@', '@@{MYSQL_PASSWORD}@@', array(
    PDO::ATTR_PERSISTENT => true,
    PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,
  ));

  // ensure there is at least 1 row of data for the app to read
  $data = $dbh->query("INSERT INTO @@{DATABASE_TABLE}@@ (id, time, message) VALUES (NULL, CURRENT_TIMESTAMP, '@@{calm_username}@@')");

  $data = $dbh->query('SELECT MAX(id) AS count FROM @@{DATABASE_TABLE}@@')->fetchAll(PDO::FETCH_COLUMN);
  $count = $data[0]; //print_r($data);
  $data = $dbh->query("SELECT time, message FROM @@{DATABASE_TABLE}@@ WHERE id = $count")->fetchAll(PDO::FETCH_ASSOC);
  $time = $data[0]['time'];
  $message = $data[0]['message'];

  echo "<p>Database records: $count</p>\n";
  echo "<p>Last maintainer: $message, on: $time</p>\n";
  echo "<h3>Web docroot: {$_SERVER["DOCUMENT_ROOT"]}, file: {$_SERVER["SCRIPT_NAME"]}</h3>\n";

  $dbh = null;
}
catch (PDOException $e) {
  print "Error!: " . $e->getMessage() . "<br/>";
  print phpinfo();
  die();
}
?></body>
</html>
EOF

#!/usr/bin/env bash
set -ex

# Assuming CentOS/RedHat 8.2
echo "__ Mysql installation..."
# https://dev.mysql.com/doc/refman/8.0/en/linux-installation-yum-repo.html

sudo yum install -y "http://repo.mysql.com/mysql80-community-release-el8.rpm" &&
  sudo yum module -y disable mysql &&
  sudo yum install -y mysql-community-server

# database must be started for SQL configuration
sudo systemctl start mysqld
# database should start on OS boot
sudo systemctl enable mysqld
# sudo systemctl status mysqld.service

echo "__ Altering database root password..."

mysql --user=root --connect-expired-password \
  --password="$(sudo grep -oP 'temporary password(.*): \K(\S+)' /var/log/mysqld.log)" <<- EOF

SET GLOBAL validate_password.policy=LOW;
ALTER user 'root'@'localhost' IDENTIFIED BY '@@{MYSQL_PASSWORD}@@';

FLUSH PRIVILEGES;
EOF

echo "__ Creating database, table, and app user permissions..."

mysql --user=root --password='@@{MYSQL_PASSWORD}@@' <<- "EOF"

CREATE DATABASE IF NOT EXISTS @@{DATABASE_NAME}@@ ;
CREATE TABLE IF NOT EXISTS @@{DATABASE_NAME}@@.@@{DATABASE_TABLE}@@ ( \
  id INT NOT NULL AUTO_INCREMENT , \
  time DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , \
  message VARCHAR(80) NULL DEFAULT NULL , \
  PRIMARY KEY (id)) \
  ENGINE = InnoDB CHARSET=ascii COLLATE ascii_general_ci COMMENT = 'testing';

FLUSH PRIVILEGES;
CREATE USER '@@{MYSQL_APP_USER}@@'@'%' IDENTIFIED WITH mysql_native_password BY '@@{MYSQL_PASSWORD}@@' \
  REQUIRE NONE WITH MAX_QUERIES_PER_HOUR 0 MAX_CONNECTIONS_PER_HOUR 0 MAX_UPDATES_PER_HOUR 0 MAX_USER_CONNECTIONS 0;
GRANT SELECT, INSERT ON *.* TO '@@{MYSQL_APP_USER}@@'@'%';

CREATE USER '@@{MYSQL_APP_USER}@@'@'localhost' IDENTIFIED WITH mysql_native_password BY '@@{MYSQL_PASSWORD}@@' \
  REQUIRE NONE WITH MAX_QUERIES_PER_HOUR 0 MAX_CONNECTIONS_PER_HOUR 0 MAX_UPDATES_PER_HOUR 0 MAX_USER_CONNECTIONS 0;
GRANT SELECT, INSERT ON *.* TO '@@{MYSQL_APP_USER}@@'@'localhost';

FLUSH PRIVILEGES;
EOF

